"""
You are given a string, s, and a list of words, words, that are all of the same length. 
Find all starting indices of substring(s) in s that is a concatenation of each word in words exactly once and without any intervening characters.

For example, given:
s: "barfoothefoobarman"
words: ["foo", "bar"]

You should return the indices: [0,9].
(order does not matter).
"""

class Solution(object):
    def findSubstring(self, s, words):
        """
        :type s: str
        :type words: List[str]
        :rtype: List[int]
        """
        import collections
        count = collections.Counter(words)
        ans = []
        word_len = len(words[0])
        words_len = len(words)
        for i in range(len(s) - word_len * words_len + 1):
            seen = collections.Counter()
            for j in range(words_len):
                word_start_index = i + word_len * j
                curr_word = s[word_start_index:word_start_index + word_len]
                if count[curr_word] != 0:
                    seen.update([curr_word])
                    if seen[curr_word] > count[curr_word]:
                        break
                    if seen[curr_word] == count[curr_word]:
                        if j + 1 == words_len:
                            ans.append(i)
                else:
                    break
        return ans

class Solution:
    # @param S, a string
    # @param L, a list of string
    # @return a list of integer
    def findSubstring(self, S, L):
        result, word_num, word_len = [], len(L), len(L[0])
        words = collections.defaultdict(int)
        for i in L:
            words[i] += 1

        for i in xrange(len(S) + 1 - word_len * word_num):
            cur, j = collections.defaultdict(int), 0
            while j < word_num:
                word = S[i + j * word_len:i + j * word_len + word_len]
                if word not in words: 
                    break
                cur[word] += 1
                if cur[word] > words[word]:
                    break
                j += 1
            if j == word_num:
                result.append(i)
        return result

